local Splix=loadstring(game:HttpGet("https://raw.githubusercontent.com/SplixDev/Splix-UI-Library/main/source.lua"))()local Window=Splix:new({name="Examination Hack",color=Color3.fromRGB(225,58,81)})Window:watermark():update({["Status"]="Active",["Player"]=game.Players.LocalPlayer.Name})local Main=Window:page({name="Main"})local MainSection=Main:section({name="Cheats",side="left",size=180})MainSection:toggle({name="ESP",def=false,callback=function(s)if s then for i,v in pairs(game.Players:GetPlayers())do if v~=game.Players.LocalPlayer and v.Character then local h=Instance.new("Highlight")h.Parent=v.Character h.FillColor=Color3.fromRGB(255,0,0)h.OutlineColor=Color3.fromRGB(255,255,255)h.Name="EXM_ESP"end end else for i,v in pairs(workspace:GetDescendants())do if v.Name=="EXM_ESP"then v:Destroy()end end end end})MainSection:toggle({name="NoClip",def=false,callback=function(s)if s then game:GetService("RunService").Stepped:Connect(function()if game.Players.LocalPlayer.Character then for i,v in pairs(game.Players.LocalPlayer.Character:GetDescendants())do if v:IsA("BasePart")then v.CanCollide=false end end end end)end end})MainSection:slider({name="Speed",def=16,min=16,max=100,rounding=true,measurement="",callback=function(v)local p=game.Players.LocalPlayer if p.Character and p.Character:FindFirstChild("Humanoid")then p.Character.Humanoid.WalkSpeed=v end end})MainSection:slider({name="Jump",def=50,min=50,max=200,rounding=true,measurement="",callback=function(v)local p=game.Players.LocalPlayer if p.Character and p.Character:FindFirstChild("Humanoid")then p.Character.Humanoid.JumpPower=v end end})local AutoSection=Main:section({name="Auto Farm",side="right",size=150})AutoSection:toggle({name="Auto Collect",def=false,callback=function(s)if s then spawn(function()while task.wait(0.5)do for i,v in pairs(workspace:GetDescendants())do if v:IsA("BasePart")and v.Name:find("Item")or v.Name:find("Collect")or v.Name:find("Coin")then if game.Players.LocalPlayer.Character and game.Players.LocalPlayer.Character:FindFirstChild("HumanoidRootPart")then game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame=v.CFrame end end end end end)end end})AutoSection:dropdown({name="Farm Mode",def="Normal",options={"Normal","Fast","Safe"},callback=function(o)end})local Visuals=Window:page({name="Visuals"})local VisualSection=Visuals:section({name="ESP Settings",side="left",size=160})VisualSection:colorpicker({name="ESP Color",def=Color3.fromRGB(255,0,0),callback=function(c)for i,v in pairs(workspace:GetDescendants())do if v.Name=="EXM_ESP"then v.FillColor=c end end end})VisualSection:multibox({name="ESP Targets",def={"Players"},options={"Players","Items","Doors"},callback=function()end})local PlayerPage=Window:page({name="Player"})local MoveSection=PlayerPage:section({name="Movement",side="left",size=140})MoveSection:toggle({name="Fly",def=false,callback=function(s)if s then local p=game.Players.LocalPlayer local c=p.Character or p.CharacterAdded:Wait()local b=Instance.new("BodyVelocity",c.HumanoidRootPart)b.Velocity=Vector3.new(0,0,0)b.MaxForce=Vector3.new(0,math.huge,0)b.Name="EXM_FLY"game:GetService("UserInputService").InputBegan:Connect(function(i)if i.KeyCode==Enum.KeyCode.Space then b.Velocity=Vector3.new(0,50,0)elseif i.KeyCode==Enum.KeyCode.LeftShift then b.Velocity=Vector3.new(0,-50,0)end end)game:GetService("UserInputService").InputEnded:Connect(function(i)if i.KeyCode==Enum.KeyCode.Space or i.KeyCode==Enum.KeyCode.LeftShift then b.Velocity=Vector3.new(0,0,0)end end)else local p=game.Players.LocalPlayer if p.Character then local b=p.Character:FindFirstChild("EXM_FLY")if b then b:Destroy()end end end end})local SettingsPage=Window:page({name="Settings"})local ConfigSection=SettingsPage:section({name="Config",side="left",size=120})ConfigSection:colorpicker({name="UI Color",def=Color3.fromRGB(225,58,81),callback=function(c)Window:settheme("accent",c)end})ConfigSection:keybind({name="UI Key",def=Enum.KeyCode.RightShift,callback=function()end})if not isfolder("ExaminationConfigs")then makefolder("ExaminationConfigs")end ConfigSection:configloader({folder="ExaminationConfigs/"})local HotkeySection=SettingsPage:section({name="Hotkeys",side="right",size=100})HotkeySection:button({name="Teleport to Spawn",callback=function()local p=game.Players.LocalPlayer if p.Character then p.Character:MoveTo(Vector3.new(0,10,0))end end})HotkeySection:button({name="Refresh ESP",callback=function()for i,v in pairs(workspace:GetDescendants())do if v.Name=="EXM_ESP"then v:Destroy()end end end})game:GetService("UserInputService").InputBegan:Connect(function(i)if i.KeyCode==Enum.KeyCode.F1 then game.Players.LocalPlayer.Character:MoveTo(Vector3.new(0,10,0))elseif i.KeyCode==Enum.KeyCode.F2 then for x,v in pairs(workspace:GetDescendants())do if v:IsA("BasePart")and v.Name:find("End")or v.Name:find("Exit")then game.Players.LocalPlayer.Character.HumanoidRootPart.CFrame=v.CFrame end end elseif i.KeyCode==Enum.KeyCode.F3 then for i,v in pairs(game.Players:GetPlayers())do if v~=game.Players.LocalPlayer and v.Character then local h=Instance.new("Highlight")h.Parent=v.Character h.FillColor=Color3.fromRGB(0,255,0)h.Name="EXM_ESP"end end end end)print("[Examination Hack] Loaded successfully!")
